code_examples.example:
# Command :- ::ixiangpf::emulation_bgp_config
# This example below will create a BGP router
set bgp_ipv4_peer_status [::ixiangpf::emulation_bgp_config \ -mode create \ -active 1 \ -handle <ipv4_handle> \ -count 10 \ -hold_time 30 \ -ip_version 4 \ -local_ip_addr 200.1.1.1 \ -md5_enable 1 \ -md5_key 34567 \ -remote_ip_addr 201.1.1.2 \ -restart_time 100 \ -retry_time 200 \ ]
# The command above will generate output as shown below: # {status 1} {bgp_handle bgp4Peerhandle}
# This example will modify the just created BGP Router by using the BGP Router handle
set bgp_modified [::ixiangpf::emulation_bgp_config \ -mode modify \ -handle <bgp_handle> \ -remote_ip_addr 100.2.0.1 \ -bfd_registration 1 \ ]
# The command above will generate output as shown below: # {status 1}
# This example will remove the BGP router
set bgp_removed [::ixiangpf::emulation_bgp_config \ -mode delete \ -handle <bgp_handle> \ ]
# The command above will generate output as shown below: # {status 1}
# This example will configure a BGP+ router
set bgp_ipv6_peer_1_status [::ixiangpf::emulation_bgp_config \ -mode enable \ -active 1 \ -md5_enable 0 \ -md5_key Ixia \ -handle <ipv6_1_handle> \ -ip_version 6 \ -remote_ipv6_addr :: \ -next_hop_enable 0 \ -next_hop_ip 0.0.0.0 \ -enable_4_byte_as 0 \ -local_as 0 \ -local_as4 0 \ -update_interval 0 \ -count 1 \ -hold_time 90 \ -neighbor_type internal \ -graceful_restart_enable 0 \ -restart_time 45 \ ]
# The command above will generate output as shown below: # {status 1} {bgp_handle bgp6PeerHandle}
