# Command :- ixiangpf.emulation_bgp_route_config # This example will configure a network group/route range for BGP router used
status = ixiangpf.emulation_bgp_route_config( handle = "<bgp_handle>", mode = "create", max_route_ranges = "5", ip_version = "4", prefix = "121.12.12.1", num_routes = "13", prefix_from = "24", flap_delay = "0", flap_down_time = "0", ) # The command above will generate output as shown below: # {'status' : '1', 'network_group_handle' : 'networkGroupHandle', # 'macpool_ip_prefix' 'prefixPools', # 'ip_routes' : 'bgpIpRouteProperty', # 'bgp_routes' : 'bgpRoutes'}
status = ixiangpf.emulation_bgp_route_config( mode = "add", handle = "<bgp_handle>", ip_version = "4", advertise_as_bgp_3107_sr = "1", segmentId = "500", incrementMode = "increment", specialLabel = "includeexplicitipv4null", enableSRGB = "0", ) # The command above will generate output as shown below: # {'status' : '1', 'network_group_handle' : 'networkGroupHandle', # 'macpool_ip_prefix' : 'ipv4Prefixpools', # 'ip_routes' : 'bgpIPRouteProperty', # 'bgp_routes' : 'bgpRoutes'}

# Command :- ixiangpf::emulation_bgp_route_config
# This example will configure a network group/route range for BGP router used
my $network_group_1_status = ixiangpf::emulation_bgp_route_config ({
    handle                                                      => "<bgp_handle>",
    mode                                                        => "create",
    max_route_ranges                                            => "5",
    ip_version                                                  => "4",
    prefix                                                      => "121.12.12.1",
    num_routes                                                  => "13",
    prefix_from                                                 => "24",
    flap_delay                                                  => "0",
    flap_down_time                                              => "0",
});
# The command above will generate output as shown below:
# {status 1} {network_group_handle networkGroupHandle} 
# {macpool_ip_prefix prefixPools} 
# {ip_routes bgpIpRouteProperty} 
# {bgp_routes {bgpRoutes}}]
my $network_groupv4_bgp_status = ixiangpf::emulation_bgp_route_config ({
    mode                                                        => "add",
    handle                                                      => "<bgp_handle>",
    ip_version                                                  => "4",
    advertise_as_bgp_3107_sr                                    => "1",
    segmentId                                                   => "500",
    incrementMode                                               => "increment",
    specialLabel                                                => "includeexplicitipv4null",
    enableSRGB                                                  => "0",
});
# The command above will generate output as shown below:
# {status 1} {network_group_handle networkGroupHandle} 
# {macpool_ip_prefix ipv4Prefixpools} 
# {ip_routes bgpIPRouteProperty} 
# {bgp_routes bgpRoutes}

# Command :- Emulation Bgp Route Config # This example will configure a network group/route range for BGP router used
${result} = Emulation Bgp Route Config handle=<bgp_handle> mode=create max_route_ranges=5 ip_version=4 prefix=121.12.12.1 ... num_routes=13 prefix_from=24 flap_delay=0 flap_down_time=0 ${status} = Get From Dictionary ${result} status Run Keyword If '${status}' != '1' FAIL "Error: Status is not SUCCESS" ELSE Log "Status is SUCCESS" ${network_group_1_status} = Get From Dictionary ${result} network_group_1_status
# The command above will generate output as shown below: # {'status' : '1', 'network_group_handle' : 'networkGroupHandle', # 'macpool_ip_prefix' 'prefixPools', # 'ip_routes' : 'bgpIpRouteProperty', # 'bgp_routes' : 'bgpRoutes'}
${result} = Emulation Bgp Route Config mode=add handle=<bgp_handle> ip_version=4 ... advertise_as_bgp_3107_sr=1 segmentId=500 incrementMode=increment specialLabel=includeexplicitipv4null enableSRGB=0 ${status} = Get From Dictionary ${result} status Run Keyword If '${status}' != '1' FAIL "Error: Status is not SUCCESS" ELSE Log "Status is SUCCESS" ${network_groupv4_bgp_status} = Get From Dictionary ${result} network_groupv4_bgp_status
# The command above will generate output as shown below: # {'status' : '1', 'network_group_handle' : 'networkGroupHandle', # 'macpool_ip_prefix' : 'ipv4Prefixpools', # 'ip_routes' : 'bgpIPRouteProperty', # 'bgp_routes' : 'bgpRoutes'}
