# Command :- ::ixiangpf::emulation_dhcp_group_config
#This command will configure a DHCPv4 Client Group set dhcp_status [::ixiangpf::emulation_dhcp_group_config \ -handle <topology_handle> \ -mac_addr 00:00:01:00:00:01 \ -mac_addr_step 00.00.00.00.00.02 \ -num_sessions 3 \ -mac_mtu 1800 \ -vlan_id 100 \ -vlan_id_step 20 \ -vlan_user_priority 2 \ -dhcp_range_ip_type ipv4 \ -dhcp_range_renew_timer 10 \ -dhcp_range_use_first_server 0 \ -use_rapid_commit 1 \ -protocol_name dhcpv4client \ -dhcp4_broadcast 1 \ ]
# This command will generate ouput as given below # {status 1} {dhcpv4client_handle dhcpV4Clienthandle} # {handle {dhcpClientItemLevelhandles}}
# This command will configure a DHCPv6 Client Group set dhcpv6client_1_status [::ixiangpf::emulation_dhcp_group_config \ -dhcp6_range_duid_enterprise_id <multivalue_handle> \ -dhcp6_range_duid_type duid_llt \ -dhcp6_range_duid_vendor_id <multivalue_3_handle> \ -dhcp6_range_ia_id 10 \ -dhcp6_range_ia_id_increment 0 \ -dhcp6_range_ia_t1 302400 \ -dhcp6_range_ia_t2 483840 \ -dhcp6_range_ia_type iana \ -dhcp6_range_max_no_per_client 1 \ -dhcp6_range_iana_count 1 \ -dhcp6_range_iapd_count 1 \ -dhcp6_range_ia_id_inc 1 \ -dhcp_range_ip_type ipv6 \ -dhcp_range_renew_timer 0 \ -handle <ethernet_handle \ -use_rapid_commit 0 \ -protocol_name {DHCPv6 Client 1} \ -enable_stateless 0 \ -dhcp6_use_pd_global_address 0 \ -use_custom_link_local_address 0 \ -custom_link_local_address <multivalue_4_handle> \ -dhcp6_gateway_address :: \ -dhcp6_gateway_mac 00.00.00.00.00.00 \ -dhcpv6_multiplier 1 \ ]
# This command will generate output as given below # {status 1} {dhcpv6client_handle dhcpv6ClientHandle} # {dhcpv6_iapd_handle dhcp6IapdHandle} # {dhcpv6_iana_handle dhcp6IanaHandle}

