# Command :- ::ixiangpf::emulation_dhcp_server_config
# This command can be used to configure DHCP Server
set dhcpv4server_1_status [::ixiangpf::emulation_dhcp_server_config \ -dhcp_offer_router_address 100.1.0.1 \ -handle <ipv4_1_handle> \ -ip_dns1 0.0.0.0 \ -ip_dns2 0.0.0.0 \ -ip_version 4 \ -ipaddress_count 10 \ -ipaddress_pool <ip_multivalue_handle> \ -ipaddress_pool_prefix_length 16 \ -ipaddress_pool_prefix_inside_step 0 \ -lease_time 86400 \ -mode create \ -protocol_name {DHCPv4 Server 1} \ -use_rapid_commit 0 \ -echo_relay_info 1 \ -pool_address_increment 0.0.0.1 \ -pool_count 1 \ -subnet_addr_assign 0 \ -subnet relay \ ]
# This command will generate output as given #{status 1} {dhcpv4server_handle dhcpv4serverHandle} #{handle dhcpServerItemHandles}
# This command wil configure a DHCPv6 Server set dhcp_status [::ixiangpf::emulation_dhcp_server_config \ -handle <deviceGroup_handle> \ -count 7 \ -dhcp6_ia_type iata \ -lease_time 86400 \ -ip_version 6 \ -protocol_name "Dhcpv6_server" \ ]
# This command will generate output as given # {status 1} {dhcpv6server_handle /topology:1/deviceGroup:1/ethernet:2/ipv6:1/dhcpv6server:1}

