# Command :- ixiangpf.emulation_igmp_config # This command will create an IGMP Host
status = ixiangpf.emulation_igmp_config( handle = "<ipv4_handle>", protocol_name = "{IGMP", mode = "create", active = "1", filter_mode = "include", general_query = "1", group_query = "1", igmp_version = "v2", ip_router_alert = "1", max_response_control = "0", unsolicited_report_interval = "120", unsolicited_response_mode = "0", join_leave_multiplier = "1", enable_iptv = "false", iptv_name = "{IPTV", stb_leave_join_delay = "0", join_latency_threshold = "5000", leave_latency_threshold = "5000", zap_behavior = "zaponly", zap_direction = "up", zap_interval_type = "leavetoleave", zap_interval = "5000", num_channel_changes_before_view = "5", view_duration = "60000", log_failure_timestamps = "0", enable_general_query_response = "false", enable_group_specific_query_response = "false", ) # The command above will generate output as shown below: # {'status' : '1', 'igmp_host_handle' : 'igmpHostHandle', # 'igmp_host_iptv_handle' : 'igmpHostIptvHandle'}
