# Command :- ixiangpf::emulation_igmp_querier_config
#This example will create an IGMP Querier my $igmp_querier_1_status = ixiangpf::emulation_igmp_querier_config ({ mode => "create", discard_learned_info => "1", active => "1", general_query_response_interval => "10000", handle => "<ipv4_handle>", igmp_version => "v2", ip_router_alert => "1", robustness_variable => "2", specific_query_response_interval => "1000", specific_query_transmission_count => "2", startup_query_count => "2", support_election => "1", support_older_version_host => "1", support_older_version_querier => "1", query_interval => "125", name => "{IGMP Querier", });
# The command above will generate output as shown below:
# {status 1} {igmp_querier_handle igmpQuerierHandle}
#This example will modify an IGMP Querier
my $igmp_querier_modified = ixiangpf::emulation_igmp_querier_config ({ mode => "modify", handle => "<igmp_querier_handle>", active => "0", });
# The command above will generate output as shown below: # {status 1}

# Command :- Emulation Igmp Querier Config #This example will create an IGMP Querier
${result} = Emulation Igmp Querier Config mode=create discard_learned_info=1 active=1 general_query_response_interval=10000 handle=<ipv4_handle> igmp_version=v2 ip_router_alert=1 robustness_variable=2 specific_query_response_interval=1000 specific_query_transmission_count=2 startup_query_count=2 support_election=1 support_older_version_host=1 support_older_version_querier=1 query_interval=125 name={IGMP ${status} = Get From Dictionary ${result} status Run Keyword If '${status}' != '1' FAIL "Error: Status is not SUCCESS" ELSE Log "Status is SUCCESS" ${igmp_querier_1_status} = Get From Dictionary ${result} igmp_querier_1_status
# The command above will generate output as shown below: # {'status' : '1' ,'igmp_querier_handle' : 'igmpQuerierHandle'}
#This example will modify an IGMP Querier
${result} = Emulation Igmp Querier Config mode=modify handle=<igmp_querier_handle> active=0 ${status} = Get From Dictionary ${result} status Run Keyword If '${status}' != '1' FAIL "Error: Status is not SUCCESS" ELSE Log "Status is SUCCESS" ${igmp_querier_modified} = Get From Dictionary ${result} igmp_querier_modified
# The command above will generate output as shown below: # {status 1}
