${result} = Emulation Mld Config mode=create filter_mode=include general_query=1 group_query=1 handle=<ipv6_handle> active=1 ip_router_alert=1 max_response_control=0 mld_version=v2 unsolicited_report_interval=120 name={MLD unsolicited_response_mode=false join_leave_multiplier=1 enable_iptv=0 iptv_name=IPTV1 iptv_name=IPTV1 ={IPTV stb_leave_join_delay=0 join_latency_threshold=5000 leave_latency_threshold=5000 zap_behavior=zaponly zap_direction=up zap_interval=5000 num_channel_changes_before_view=5 view_duration=60000 zap_interval_type=leavetoleave log_failure_timestamps=0 enable_general_query_response=false enable_group_specific_query_response=false ${status} = Get From Dictionary ${result} status Run Keyword If '${status}' != '1' FAIL "Error: Status is not SUCCESS" ELSE Log "Status is SUCCESS" ${mld_host_1_status} = Get From Dictionary ${result} mld_host_1_status
