code_examples.example:
# Command :- ::ixiangpf::multivalue_config
# This example will configure a multivalue filed of type IP with direction increment
set multivalue_status [::ixiangpf::multivalue_config \ -pattern counter \ -counter_start 100.1.0.1 \ -counter_step 0.0.1.0 \ -counter_direction increment \ -nest_step 0.1.0.0 \ -nest_owner <topology_handle> \ -nest_enabled 1 \ ]
# The command above will generate output as shown below: # {status 1} {multivalue_handle multivalueHandle}
# This example will configure a multivalue filed of type IPv6 with direction decrement
set multivalue_status [::ixiangpf::multivalue_config \ -pattern counter \ -counter_start 2000::f \ -counter_step ::0.0.0.1 \ -counter_direction decrement \ -nest_step ::0.0.0.1 \ -nest_owner <topology_handle> \ -nest_enabled 0 \ ]
# The command above will generate output as shown below: # {status 1} {multivalue_handle multivalueHandle}
# This example will configure a multivalue field with some filed being overlay.
set multivalue_status [::ixiangpf::multivalue_config \ -pattern counter \ -counter_start 100.1.0.1 \ -counter_step 0.0.1.0 \ -counter_direction increment \ -nest_step 0.1.0.0 \ -nest_owner <topology_handle> \ -nest_enabled 1 \ -overlay_value 100.1.22.1,100.1.23.1 \ -overlay_value_step 100.1.22.1,100.1.23.1 \ -overlay_index 5,6 \ -overlay_index_step 0,0 \ -overlay_count 1,1 \ ]
# The command above will generate output as shown below: # {status 1} {multivalue_handle multivalueHandle}
